<style>
#counting-number {
    font-size: 100px; /* Adjust the font size as needed */
    animation: countUp 2s ease-in-out forwards;
    opacity: 0;
}
#available-count {
    font-size: 100px; /* Adjust the font size as needed */
    animation: countUp 2s ease-in-out forwards;
    opacity: 0;
}

@keyframes countUp {
    from {
        opacity: 0;
        transform: translateY(-20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}
</style>
<script>
export default{
    data(){
        return {
            avaiable_cars : 0,
            booked_cars :0,
        }
    },
    methods:{
        getAvaiableCars(){
            const url = process.env.MIX_ADMIN_APP_URL + '/avaiable_cars';
            axios.get(url).then((res) => {
               this.avaiable_cars = res?.data?.available_cars;
               this.booked_cars = res?.data?.booked_cars;
            });
        }
    },
    created(){
        this.getAvaiableCars();
    }
}
</script>
